    updated_by OPTIONS(description='The user who last updated the Readme')
)
OPTIONS(description='Sanitized Readme asset view that decodes URL-encoded text and removes HTML tags for readability')
AS
SELECT
    guid AS guid,
    typename AS asset_type,
    name AS asset_name,
    -- Step 1: replace common URL encodings
    REGEXP_REPLACE(
      REGEXP_REPLACE(
        REGEXP_REPLACE(
          REGEXP_REPLACE(
            REGEXP_REPLACE(
              REGEXP_REPLACE(
                REGEXP_REPLACE(
                  REGEXP_REPLACE(
                    REGEXP_REPLACE(
                      REGEXP_REPLACE(
                        REGEXP_REPLACE(
                          REGEXP_REPLACE(
                            REGEXP_REPLACE(
                              REGEXP_REPLACE(
                                REGEXP_REPLACE(
                                  REGEXP_REPLACE(
                                    REGEXP_REPLACE(
                                      REGEXP_REPLACE(
                                        DESCRIPTION,
                                        '%20', ' '
                                      ),
                                      '%0A', '\n'
                                    ),
                                    '%3C', '<'
                                  ),
                                  '%2A', '*'
                                ),
                                '%22', '"'
                              ),
                              '%3E', '>'
                            ),
                            '%2F', '/'
                          ),
                          '%3A', ':'
                        ),
                        '%2C', ','
                      ),
                      '%28', '('
                    ),
                    '%29', ')'
                  ),
                  '%5B', '['
                ),
                '%5D', ']'
              ),
              '%3B', ';'
            ),
            '%2B', '+'
          ),
          '%23', '#'
        ),
        '%2E', '.'
      ),
      '<[^>]*>', ''    -- Step 2: strip HTML tags
    ) AS description,
    status AS status,
    createtime AS created_time,
    updatetime AS updated_time,
    createdby AS created_by,
    updatedby AS updated_by
FROM `development-platform-370010.atlan_wh_us_east_1.Readme_entity`;

