CREATE OR REPLACE VIEW ATLAN.ATLAN_GOLD.README (
    guid COMMENT 'The asset’s globally-unique identifier',
    asset_type COMMENT 'The type of asset (typically Readme)',
    asset_name COMMENT 'The name of the Readme asset',
    description COMMENT 'The sanitized content of the Readme, decoded from URL encoding and stripped of HTML tags',
    status COMMENT 'The status of the asset (e.g., ACTIVE, DELETED)',
    created_time COMMENT 'The timestamp when the Readme was created',
    updated_time COMMENT 'The timestamp when the Readme was last updated',
    created_by COMMENT 'The user who created the Readme',
    updated_by COMMENT 'The user who last updated the Readme'
)
COMMENT 'Sanitized Readme asset view that decodes URL-encoded text and removes HTML tags for readability'
AS
SELECT
    guid AS guid,
    typename AS asset_type,
    name AS asset_name,
    -- Step 1: replace common URL encodings
    REGEXP_REPLACE(
      REGEXP_REPLACE(
        REGEXP_REPLACE(
          REGEXP_REPLACE(
            REGEXP_REPLACE(
              REGEXP_REPLACE(
                REGEXP_REPLACE(
                  REGEXP_REPLACE(
                    REGEXP_REPLACE(
                      REGEXP_REPLACE(
                        REGEXP_REPLACE(
                          REGEXP_REPLACE(
                            REGEXP_REPLACE(
                              REGEXP_REPLACE(
                                REGEXP_REPLACE(
                                  REGEXP_REPLACE(
                                    REGEXP_REPLACE(
                                      REGEXP_REPLACE(
                                        DESCRIPTION,
                                        '%20', ' '
                                      ),
                                      '%0A', '\n'
                                    ),
                                    '%3C', '<'
                                  ),
                                  '%2A', '*'
                                ),
                                '%22', '"'
                              ),
                              '%3E', '>'
                            ),
                            '%2F', '/'
                          ),
                          '%3A', ':'
                        ),
                        '%2C', ','
                      ),
                      '%28', '('
                    ),
                    '%29', ')'
                  ),
                  '%5B', '['
                ),
                '%5D', ']'
              ),
              '%3B', ';'
            ),
            '%2B', '+'
          ),
          '%23', '#'
        ),
        '%2E', '.'
      ),
      '<[^>]*>', ''    -- Step 2: strip HTML tags
    ) AS description,
    status AS status,
    createtime AS created_time,
    updatetime AS updated_time,
    createdby AS created_by,
    updatedby AS updated_by
FROM Readme_entity;


CREATE OR REPLACE MATERIALIZED VIEW ATLAN.ATLAN_GOLD.ASSETS
(
guid COMMENT 'The asset’s globally-unique identifier',
asset_type COMMENT 'The asset’s type',
asset_name COMMENT 'The asset’s name',
asset_qualified_name COMMENT 'The asset’s unique, fully-qualified name',
description COMMENT 'The asset’s description',
readme_guid COMMENT 'The asset’s readme guid',
status COMMENT 'The asset’s status (e.g., active, archived)',
created_at COMMENT 'The time (epoch) at which the asset was created, in milliseconds',
created_by COMMENT 'The user or account that created the asset',
updated_at COMMENT 'The time (epoch) at which the asset was last updated, in milliseconds',
updated_by COMMENT 'The time (epoch) at which the asset was last updated, in milliseconds',
certificate_status COMMENT 'The asset`s certification status',
certificate_updated_by COMMENT 'The user or account that last updated the asset’s certification status',
certificate_updated_at COMMENT 'The time (epoch) at which the asset’s certification status was last updated, in milliseconds',
connector_name COMMENT 'The type of connector through which this asset is accessible',
connector_qualified_name COMMENT 'The unique, fully-qualified name of the connection through which this asset is accessible', 
source_created_at COMMENT 'Time (epoch) at which this asset was created in the source system, in milliseconds',
source_created_by COMMENT 'Name of the user who created this asset in the source system',
source_updated_at COMMENT 'Time (epoch) at which this asset was last updated in the source system, in milliseconds',
source_updated_by COMMENT 'Name of the user who last updated this asset, in the source system',
owner_users COMMENT 'List of users who own this asset',
term_guids COMMENT 'List of glossary term GUIDs associated with the asset',
popularity_score COMMENT 'The asset’s popularity score',
tags COMMENT 'List of tags attached to this asset',
has_lineage COMMENT 'Whether this asset has lineage (true) or not (false)'
)
COMMENT 'A unified asset registry view that consolidates all assets across SQL, BI, pipeline, data quality, and object domains. It serves as the central lookup source for resolving GUIDs to human-readable names, types, and associated metadata including tags and ownership details.'
AS
WITH tag_agg AS (
    SELECT
        asset_guid,
        ARRAY_AGG(DISTINCT tag_name) AS tag_names
    FROM ATLAN.ATLAN_GOLD.TAGS
    GROUP BY asset_guid
)

