CREATE OR REPLACE VIEW ATLAN.ATLAN_GOLD.LINEAGE_EDGES (
    process_guid COMMENT 'The globally-unique identifier of the process that connects the input and output assets',
    input_guid COMMENT 'The globally-unique identifier of the upstream (source) asset',
    output_guid COMMENT 'The globally-unique identifier of the downstream (target) asset'
)
COMMENT 'Internal view used to derive the lineage across various process types including standard, BI, column, and DBT processes'
AS

SELECT 
    p.guid AS process_guid,
    f.col AS input_guid,
    o.col AS output_guid
FROM process_entity p
LATERAL VIEW EXPLODE(p.inputs) f
LATERAL VIEW EXPLODE(p.outputs) o
WHERE p.status = 'ACTIVE'

UNION ALL

SELECT 
    p.guid AS process_guid,
    f.col AS input_guid,
    o.col AS output_guid
FROM biprocess_entity p
LATERAL VIEW EXPLODE(p.inputs) f
LATERAL VIEW EXPLODE(p.outputs) o
WHERE p.status = 'ACTIVE'

UNION ALL

SELECT 
    p.guid AS process_guid,
    f.col AS input_guid,
    o.col AS output_guid
FROM columnprocess_entity p
LATERAL VIEW EXPLODE(p.inputs) f
LATERAL VIEW EXPLODE(p.outputs) o
WHERE p.status = 'ACTIVE'

UNION ALL

SELECT 
    p.guid AS process_guid,
    f.col AS input_guid,
    o.col AS output_guid
FROM dbtprocess_entity p
LATERAL VIEW EXPLODE(p.inputs) f
LATERAL VIEW EXPLODE(p.outputs) o
WHERE p.status = 'ACTIVE'

UNION ALL

SELECT 
    p.guid AS process_guid,
    f.col AS input_guid,
    o.col AS output_guid
FROM dbtcolumnprocess_entity p
LATERAL VIEW EXPLODE(p.inputs) f
LATERAL VIEW EXPLODE(p.outputs) o
WHERE p.status = 'ACTIVE';


CREATE OR REPLACE MATERIALIZED VIEW ATLAN.ATLAN_GOLD.BASE_EDGES 
COMMENT 'Internal table used for flattening lineage edges by resolving GUIDs to human-readable names and types from the asset registry'
AS
SELECT DISTINCT
    e.process_guid,
    e.input_guid,
    COALESCE(i.asset_name, e.input_guid) AS input_name,
    COALESCE(i.asset_type, 'unknown') AS input_type,
    e.output_guid,
    COALESCE(o.asset_name, e.output_guid) AS output_name,
    COALESCE(o.asset_type, 'unknown') AS output_type
FROM ATLAN.ATLAN_GOLD.LINEAGE_EDGES e
LEFT JOIN (
    SELECT DISTINCT guid, asset_name, asset_type
    FROM ATLAN.ATLAN_GOLD.ASSETS
) i ON e.input_guid = i.guid
LEFT JOIN (
    SELECT DISTINCT guid, asset_name, asset_type
    FROM ATLAN.ATLAN_GOLD.ASSETS
) o ON e.output_guid = o.guid
WHERE e.input_guid IS NOT NULL 
  AND e.output_guid IS NOT NULL;

CREATE OR REPLACE VIEW ATLAN.ATLAN_GOLD.LINEAGE (
    direction COMMENT 'The flow of the lineage traversal: UPSTREAM (sources) or DOWNSTREAM (targets)',
    start_guid COMMENT 'The globally-unique identifier of the asset for which lineage is being calculated',
    start_name COMMENT 'The human-readable name of the starting asset',
    start_type COMMENT 'The asset type of the starting asset',
    related_guid COMMENT 'The globally-unique identifier of the asset found during traversal',
    related_name COMMENT 'The human-readable name of the related asset',
    related_type COMMENT 'The asset type of the related asset',
    connecting_guid COMMENT 'The intermediate GUID that links the current hop to the previous asset in the chain',
    level COMMENT 'The distance (number of hops) between the starting asset and the related asset'
)
COMMENT 'A recursive lineage view used to list the complete multi-hop lineage (upstream and downstream) of any given asset by traversing the flattened edges registry.'
AS
WITH RECURSIVE

DOWNSTREAM AS (
    SELECT
        input_guid AS start_guid,
        input_name AS start_name,
        input_type AS start_type,
        output_guid AS related_guid,
        output_name AS related_name,
        output_type AS related_type,
        input_guid AS connecting_guid,
        1 AS level,
        input_guid || ',' || output_guid AS path_str
    FROM ATLAN.ATLAN_GOLD.BASE_EDGES

    UNION ALL

    SELECT
        d.start_guid,
        d.start_name,
        d.start_type,
        e.output_guid AS related_guid,
        e.output_name AS related_name,
        e.output_type AS related_type,
        d.related_guid AS connecting_guid,
        d.level + 1 AS level,
        d.path_str || ',' || e.output_guid AS path_str
    FROM DOWNSTREAM d
    JOIN ATLAN.ATLAN_GOLD.BASE_EDGES e
        ON d.related_guid = e.input_guid
    WHERE LOCATE(e.output_guid, d.path_str) = 0
),

UPSTREAM AS (
    SELECT
        output_guid AS start_guid,
        output_name AS start_name,
        output_type AS start_type,
        input_guid AS related_guid,
        input_name AS related_name,
        input_type AS related_type,
        output_guid AS connecting_guid,
        1 AS level,
        output_guid || ',' || input_guid AS path_str
    FROM ATLAN.ATLAN_GOLD.BASE_EDGES

    UNION ALL

    SELECT
        u.start_guid,
        u.start_name,
        u.start_type,
        e.input_guid AS related_guid,
        e.input_name AS related_name,
        e.input_type AS related_type,
        u.related_guid AS connecting_guid,
        u.level + 1 AS level,
        u.path_str || ',' || e.input_guid AS path_str
    FROM UPSTREAM u
    JOIN ATLAN.ATLAN_GOLD.BASE_EDGES e
        ON u.related_guid = e.output_guid
    WHERE LOCATE(e.input_guid, u.path_str) = 0
)

SELECT DISTINCT
    'DOWNSTREAM' AS direction,
    start_guid,
    start_name,
    start_type,
    related_guid,
    related_name,
    related_type,
    connecting_guid,
    level
FROM DOWNSTREAM
WHERE related_guid IS NOT NULL

UNION ALL

SELECT DISTINCT
    'UPSTREAM' AS direction,
    start_guid,
    start_name,
    start_type,
    related_guid,
    related_name,
    related_type,
    connecting_guid,
    level
FROM UPSTREAM
WHERE related_guid IS NOT NULL;




