CREATE OR REPLACE VIEW ATLAN_GOLD.PUBLIC.TAGS 
(
    asset_guid COMMENT 'The asset’s globally-unique identifier',
    asset_type COMMENT 'The asset’s type',
    tag_guid COMMENT 'The tag’s globally-unique identifier',
    tag_name COMMENT 'The tag’s name',
    tag_value COMMENT 'The tag’s value',
    tag_connector_name COMMENT 'The type of connector where this tag originates from, if applicable',
    propagate COMMENT 'Whether or not the tag propagates through hierarchy or parent-child relationships (True or False)'
)
COMMENT = 'Information about tagged assets in Atlan'
AS
SELECT
    entityguid AS asset_guid,
    entitytypename AS asset_type,
    tagguid AS tag_guid,
    tagname AS tag_name,
    tagvalue AS tag_value,
    tagconnectorname AS tag_connector_name,
    propagate AS propagate
FROM ATLAN_CONTEXT_STORE."entity_metadata".TagRelationship;


