CREATE OR REPLACE VIEW ATLAN_GOLD.PUBLIC.README (
    guid COMMENT 'The asset’s globally-unique identifier',
    asset_type COMMENT 'The type of asset (typically Readme)',
    asset_name COMMENT 'The name of the Readme asset',
    description COMMENT 'The sanitized content of the Readme, decoded from URL encoding and stripped of HTML tags',
    status COMMENT 'The status of the asset (e.g., ACTIVE, DELETED)',
    created_time COMMENT 'The timestamp when the Readme was created',
    updated_time COMMENT 'The timestamp when the Readme was last updated',
    created_by COMMENT 'The user who created the Readme',
    updated_by COMMENT 'The user who last updated the Readme'
)
COMMENT = 'Sanitized Readme asset view that decodes URL-encoded text and removes HTML tags for readability'
AS
SELECT
    guid AS guid,
    typename AS asset_type,
    name AS asset_name,
    -- Step 1: replace common URL encodings
    REGEXP_REPLACE(
      REGEXP_REPLACE(
        REGEXP_REPLACE(
          REGEXP_REPLACE(
            REGEXP_REPLACE(
              REGEXP_REPLACE(
                REGEXP_REPLACE(
                  REGEXP_REPLACE(
                    REGEXP_REPLACE(
                      REGEXP_REPLACE(
                        REGEXP_REPLACE(
                          REGEXP_REPLACE(
