CREATE OR REPLACE TASK ATLAN_GOLD.PUBLIC.REFRESH_BASE_EDGES
  SCHEDULE = 'USING CRON 0 * * * * UTC'
AS
 INSERT OVERWRITE INTO ATLAN_GOLD.PUBLIC.BASE_EDGES
 SELECT DISTINCT
    e.process_guid,
    e.input_guid,
    COALESCE(i.asset_name, e.input_guid) AS input_name,
    COALESCE(i.asset_type, 'unknown') AS input_type,
    e.output_guid,
    COALESCE(o.asset_name, e.output_guid) AS output_name,
    COALESCE(o.asset_type, 'unknown') AS output_type
FROM ATLAN_GOLD.PUBLIC.LINEAGE_EDGES e
LEFT JOIN (
    SELECT DISTINCT guid, asset_name, asset_type
    FROM ATLAN_GOLD.PUBLIC.ASSETS
) i ON e.input_guid = i.guid
LEFT JOIN (
    SELECT DISTINCT guid, asset_name, asset_type
    FROM ATLAN_GOLD.PUBLIC.ASSETS
) o ON e.output_guid = o.guid
WHERE e.input_guid IS NOT NULL 
  AND e.output_guid IS NOT NULL;
  
ALTER TASK ATLAN_GOLD.PUBLIC.REFRESH_ASSETS RESUME;
ALTER TASK ATLAN_GOLD.PUBLIC.REFRESH_BASE_EDGES RESUME;
